import { Component } from "@angular/core";

@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.css"],
})
export class AppComponent {
  currentName: string = "";
  nameArray: string[] = [];

  addName() {
    this.nameArray.push(this.currentName);
  }

  inputName(name: string) {
    this.currentName = name;
  }
}
