import { Component } from "@angular/core";

type Team = {
  value: number;
  names: string[];
};
@Component({
  selector: "app-root",
  templateUrl: "./app.component.html",
  styleUrls: ["./app.component.css"],
})
export class AppComponent {
  currentName: string = "";
  nameArray: string[] = [];
  errorMessage: string = "";
  teams: Team[] = [];

  addName() {
    if (!this.currentName) {
      this.errorMessage = "Nome não pode ser vazio!";
      return;
    }
    this.nameArray.push(this.currentName);
    this.currentName = "";
    this.errorMessage = "";
  }

  inputName(name: string) {
    this.currentName = name;
  }

  nameRandomizr(nameArray: string[]) {
    return nameArray.sort();
  }

  generateTeams(qtd: number) {
    if (qtd > this.nameArray.length) {
      this.errorMessage = "Não há nomes o suficiente!";
      return;
    }
  }
}
